import React, { useEffect, useState } from "react";
import "../sass/main.css";
import "../../node_modules/bootstrap/dist/css/bootstrap.css";
import "../../node_modules/bootstrap/dist/js/bootstrap.bundle";

import getList from "../services/lists";
import setItem from "../services/postlist";

export default function Home() {

  const [itemInput, setItemInput] = useState({setname:'',seturl:''});

  const [list, setList] = useState([]);
  const [visibleBlogs, setVisibleBlogs] = useState([3]);

  const handleClick = () => {
    setVisibleBlogs((prevVisibleBlogs) => prevVisibleBlogs + 3);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setItem(itemInput);
  };

  useEffect(() => {
    let mounted = true;
    getList().then((items) => {
      if (mounted) {
        setList(items);
      }
    });
    return () => (mounted = false);
  }, []);

  return (
    <>
      <div class="container" style={{ paddingTop: "10px" }}>
        <div class="row">
          <div class="col-sm">
            <div class="card" style={{ width: "18rem" }}>
              <div class="card-body">
                <div class="mb-3">
                  <form onSubmit={handleSubmit}>
                    <label for="exampleFormControlInput1" class="form-label">
                      New Card
                    </label>
                    <div class="input-group mb-3">
                      <select
                        class="form-select"
                        aria-label="Default select example"
                        onChange={(event) => setItemInput(itemInput.seturl.setevent.target.value)}
                        value={itemInput.seturl}
                      >
                        <option selected>Open this select menu</option>
                        <option value="https://c.tenor.com/w9aq-JnqRm8AAAAj/naruto-eat.gif">image 1</option>
                        <option value="#">image 2</option>
                        <option value="#">image 3</option>
                      </select>
                      <label class="input-group-text" for="inputGroupSelect02">
                        Url Image
                      </label>
                    </div>
                    <input
                      type="text"
                      class="form-control"
                      id="exampleFormControlInput1"
                      placeholder="Your Name"
                      onChange={(event) => setItemInput(event.target.value)}
                      value={itemInput.setname}
                    />
                    <button
                      type="submit"
                      class="btn btn-secondary"
                      style={{ marginTop: "10px" }}
                    >
                      Submit
                    </button>
                  </form>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      <div class="container" style={{ paddingTop: "10px" }}>
        <div class="row">
          {list.slice(0, visibleBlogs).map((item, i) => (
            <div class="col-sm">
              <div class="card" style={{ width: "18rem" }}>
                <div class="card-body">
                  <h5 class="card-title">{list[i].name}</h5>
                  <p class="card-text">
                    <img src={list[i].url} style={{ width: "13rem" }} />
                  </p>
                  <a href="#" class="btn btn-primary">
                    Go somewhere
                  </a>
                </div>
              </div>
            </div>
          ))}
        </div>
        <button
          type="button"
          class="btn btn-outline-success"
          style={{ marginTop: "10px" }}
          onClick={handleClick}
        >
          Load More..
        </button>
      </div>
    </>
  );
}
