import axios from 'axios'

export const GET_REQUEST = "GET_REQUEST";
export const GET_REQUEST_API_SUCCESS = "GET_REQUEST_API";
export const GET_REQUEST_API_FAILURE = "GET_REQUEST_API_FAILURE";

const getRequest = () => {
    return {
      type: GET_REQUEST,
    };
  };

const getrequestAPIsuccess = users => {
    return {
      type: GET_REQUEST_API_SUCCESS,
      payload: users
    };
  };

const getrequestAPIfailure = error => {
    return {
      type: GET_REQUEST,
      payload: error
    };
  };  

 export default function fetchPlanets() {
      return (dispatch) => {

        dispatch(getRequest);

        axios.get('http://localhost:3333/person')
          .then((response) => dispatch(getrequestAPIsuccess(response.json())))

          .catch((error) => dispatch(getrequestAPIfailure(error.message)));
      };
    }
