import axios from "axios";

export const GET_REQUEST = "GET_REQUEST";
export const GET_REQUEST_API_SUCCESS = "GET_REQUEST_API";
export const GET_REQUEST_API_FAILURE = "GET_REQUEST_API_FAILURE";

export const getRequest = () => {
    return {
      type: GET_REQUEST,
    };
  };

  const getrequestAPIsuccess = users => {
    return {
      type: GET_REQUEST_API_SUCCESS,
      payload: users
    };
  };

  const getrequestAPIfailure = error => {
    return {
      type: GET_REQUEST_API_FAILURE,
      payload: error
    };
  };  

export default function getAPI() {

  return (dispatch) => {
    dispatch(getRequest);

    axios
      .get("http://localhost:3000/home")
      .then((response) => {
        const users = response;
        dispatch(getrequestAPIsuccess(users));
      })
      .then((err) => {
        const errorMsg = err.message
        dispatch(getrequestAPIfailure(errorMsg));
      });
  };

}

