export const GET_REQUEST = "GET_REQUEST";
export const GET_REQUEST_API_SUCCESS = "GET_REQUEST_API";
export const GET_REQUEST_API_FAILURE = "GET_REQUEST_API_FAILURE";

const getRequest = () => {
    return {
      type: GET_REQUEST,
    };
  };

const getrequestAPIsuccess = users => {
    return {
      type: GET_REQUEST_API_SUCCESS,
      payload: 1
    };
  };

const getrequestAPIfailure = error => {
    return {
      type: GET_REQUEST,
      payload: error
    };
  };  

  let PlanetAction = {
    fetchPlanets() {
      return (dispatch) => {
        dispatch({ type: REQUEST_PLANETS });

        axios.get('/static/solar-system-planets.json')
          .then((response) => dispatch({
            type: RECEIVE_PLANETS,
            success: true,
            planets: response.data.planets
          }))

          .catch((error) => dispatch({
            type: RECEIVE_PLANETS,
            error: error.message,
            planets: null
          }));
      };
    }
  };
