import React, { useEffect, useState } from "react";
import "../sass/main.css";
import "../../node_modules/bootstrap/dist/css/bootstrap.css";
import "../../node_modules/bootstrap/dist/js/bootstrap.bundle";

import getList from "../services/lists";
import setItem from "../services/postlist";

export default function Home() {



  const [alert, setAlert] = useState(false);
  const [itemInput, setItemInput] = useState({ setname: "", seturl: "" });
  const [list, setList] = useState([{}]);
  const [visibleBlogs, setVisibleBlogs] = useState([3]);

  const initalstate = {
     setname:"",
     seturl:""
  }

  const handleClick = () => {
    setVisibleBlogs((prevVisibleBlogs) => prevVisibleBlogs + 3);
  };

  const handleSubmit = (e) => {

    e.preventDefault();
    setItem(itemInput).then(() => {

      setItemInput({...initalstate});
      setAlert(true);

    });
  };

  const handlerChange = (event) => {
    setItemInput((prevState) => ({
      ...prevState,
      [event.target.id]: event.target.value,
    }));

    console.log(setItemInput((prevState) => ({ ...prevState })));
  };

  useEffect(() => {
    let mounted = true;
    getList().then((items) => {
      if (mounted) {
        setList(items);
      }
    });
    return () => (mounted = false);
  }, []);

  return (
    <>
      <div class="container" style={{ paddingTop: "10px" }}>
        <div class="row">
          <div class="col-sm">
            <div class="card">
              <div class="card-body">
                <div class="mb-3">
                  <form onSubmit={handleSubmit}>
                    <label for="exampleFormControlInput1" class="form-label">
                      New Card
                    </label>
                    <input
                      type="text"
                      class="form-control"
                      placeholder="Name"
                      id="setname"
                      onChange={handlerChange}
                      value={itemInput.setname}
                      style={{ marginBottom: "10px" }}
                      required
                    />
                    <input
                      type="text"
                      class="form-control"
                      placeholder="Url Image"
                      id="seturl"
                      onChange={handlerChange}
                      value={itemInput.seturl}
                      required
                    />
                    <button
                      type="submit"
                      class="btn btn-secondary"
                      style={{ marginTop: "10px" }}
                    >
                      Submit
                    </button>
                  </form>
                </div>
              </div>
              {alert && (
                <div
                  class="alert alert-success alert-dismissible fade show"
                  role="alert"
                >
                  <strong>Successed Input!</strong>
                  <button
                    type="button"
                    class="btn-close"
                    data-bs-dismiss="alert"
                    aria-label="Close"
                  ></button>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>

      <div class="container" style={{ paddingTop: "10px" }}>
        <div class="row">
          {list.slice(0, visibleBlogs).map((item, i) => (
            <div class="col-sm">
              <div class="card" style={{ width: "18rem" }}>
                <div class="card-body">
                  <h5 class="card-title">{list[i].setname}</h5>
                  <p class="card-text">
                    <img src={list[i].seturl} style={{ width: "13rem" }} />
                  </p>
                  <a href="#" class="btn btn-primary">
                    Go somewhere
                  </a>
                </div>
              </div>
            </div>
          ))}
        </div>
        <button
          type="button"
          class="btn btn-outline-success"
          style={{ marginTop: "10px" }}
          onClick={handleClick}
        >
          Load More..
        </button>
      </div>
    </>
  );
}
