//post resquest with react redux thunk
//get request with react redux thunk+


import "./sass/main.css";
import "../node_modules/bootstrap/dist/css/bootstrap.css";
import "../node_modules/bootstrap/dist/js/bootstrap.bundle";

import React, { useEffect, useState } from "react";
import { postUsers } from "../src/redux/actions";
import { useDispatch, useSelector } from "react-redux";

function App() {

  const dispatch = useDispatch();
  const eventsInfo = useSelector((state) => state.users);
  const [itemInput, setItemInput] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch(postUsers(itemInput));
  };



  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label>
          <p>New Item</p>
          <input type="text" onChange={event => setItemInput(event.target.value)} value={itemInput} />
        </label>
        <button type="submit">Submit</button>
      </form>
      {console.log(eventsInfo)}
    </div>
  );
}

export default App;
