import React, { useEffect, useState } from "react";
import "../sass/main.css";
import "../../node_modules/bootstrap/dist/css/bootstrap.css";
import "../../node_modules/bootstrap/dist/js/bootstrap.bundle";

import getList from "../services/lists";

export default function Home() {

  const [list, setList] = useState([]);
  const [visibleBlogs, setVisibleBlogs] = useState([2])

  const handleClick = () => {
    setVisibleBlogs(prevVisibleBlogs => prevVisibleBlogs + 3)
 }

  useEffect(() => {
    let mounted = true;
    getList().then((items) => {
      if (mounted) {
        setList(items);
      }
    });
    return () => (mounted = false);
  }, []);

  return (
    <>
      <div class="container" style={{ paddingTop: "10px" }}>
        <div class="row">
          {list.slice(0, visibleBlogs).map((item,i) => (
            <div class="col-sm">
              <div class="card" style={{ width: "18rem" }}>
                <div class="card-body">
                  <h5 class="card-title">{list[i].name}</h5>
                  <p class="card-text"><img src={list[i].url} style={{ width: "13rem" }}/></p>
                  <a href="#" class="btn btn-primary">
                    Go somewhere
                  </a>
                </div>
              </div>
            </div>
          ))}
        </div>
        <button type="button" class="btn btn-outline-success" style={{}} onClick={handleClick}>Load More..</button>
      </div>

    </>
  );
}
