import React from "react";
import ReactDOM from "react-dom";
import "./index.css";
import reportWebVitals from "./reportWebVitals";
import App from "./App";
import { Provider } from "react-redux";
import { createStore, applyMiddleware } from "redux";
import thunk from "redux-thunk";
import todosReducer from "../src/reducer/lists";

import { useDispatch, useSelector } from 'react-redux';

// use applyMiddleware to add the thunk middleware to the store
const store = createStore(todosReducer, applyMiddleware(thunk));

function Getperson(){

  store.subscribe(() => {
    if (store.getState().action.indexOf('counter') !== -1) {
        console.log('subscribed for messanger actions', store.getState());
    }
});

  return (
    <>
      Views: {console.log(store.subscribe())}
      <button onClick={() => dispatch({ type: "ADD_TODO_SUCCESS" })}>
        <span role="img" aria-label="add">
          âž•
        </span>
      </button>
    </>
  );
};

console.log("current state:", store.getState());

ReactDOM.render(
  <React.StrictMode>
    <Provider store={store}>
      <Getperson />
    </Provider>
  </React.StrictMode>,
  document.getElementById("root")
);

// If you want to start measuring performance in your app, pass a function
