import axios from 'axios';

export const ADD_PERSON_SUCCESS = 'ADD_PERSON_SUCCESS';
export const ADD_PERSON_FAILURE = 'ADD_PERSON_FAILURE';


const getTodo = () => {

  axios
    .get(`http://localhost:3333/person`, {
    })
    .then(res => {
      addTodoSuccess(res);
    })
    .catch(err => {
     addTodoFailure(err.message);
    });

};

const addTodoSuccess = (todo) => ({
  type: ADD_PERSON_SUCCESS,
  payload: todo
});

const addTodoFailure = error => ({
  type: ADD_PERSON_FAILURE,
  payload: {
    error
  }
});

const initialState = {
  setname: "",
  seturl: "",
  todos:[]
};

export default function todosReducer(state = initialState, action) {
  switch (action.type) {
    case ADD_PERSON_SUCCESS:
      return {
        ...state,
        todos:action.payload,
        success: 'true'

      };
    case ADD_PERSON_FAILURE:
      return {
        ...state,
        failed: 'false'
      };
    default:
      return state;
  }
 }
