//post resquest with react redux thunk
//get request with react redux thunk+useeffect

import "./sass/main.css";
import "../node_modules/bootstrap/dist/css/bootstrap.css";
import "../node_modules/bootstrap/dist/js/bootstrap.bundle";
import React, { useState } from "react";
import { BrowserRouter, Route, Switch } from "react-router-dom";
import axios from "axios";

const apiUrl = "http://localhost:3001";

async function loginUser(credentials) {
  return axios
    .get(this.BASE_URL + "/profile/me", {
      headers: { Authorization: `Bearer ${JWTToken}` },
    })
    .then((res) => {
      res.data;
      console.log("profile is:", res.data);
    })
    .catch((error) => console.log(error));
}

function jwtRequest(url, token) {
  var req = new XMLHttpRequest();
  req.open("get", url, true);
  req.setRequestHeader("Authorization", "Bearer " + token);
  req.send();
}

jwtRequest("/api/example", localStorage.jwt);

function App() {
  const storedJwt = localStorage.getItem("token");

  const [jwt, setJwt] = useState(storedJwt || null);
  const [foods, setFoods] = useState([]);
  const [fetchError, setFetchError] = useState(null);

  const getJwt = async () => {
    const { data } = await axios.get(`${apiUrl}/jwt`);
    localStorage.setItem("token", data.token);
    setJwt(data.token);
  };

  const getFoods = async () => {
    try {
      const { data } = await axios.get(`${apiUrl}/foods`);
      setFoods(data);
      setFetchError(null);
    } catch (err) {
      setFetchError(err.message);
    }
  };

  return (
    <>
      <section style={{ marginBottom: "10px" }}>
        <button onClick={() => getJwt()}>Get JWT</button>
        {jwt && (
          <pre>
            <code>{jwt}</code>
          </pre>
        )}
      </section>
      <section>
        <button onClick={() => getFoods()}>Get Foods</button>
        <ul>
          {foods.map((food, i) => (
            <li>{food.description}</li>
          ))}
        </ul>
        {fetchError && <p style={{ color: "red" }}>{fetchError}</p>}
      </section>
    </>
  );
}

export default App;
