import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import reportWebVitals from './reportWebVitals';
import '.'


class Conditional extends React.Component{
  constructor(props) {
    super(props);
    this.handleturnOnClick = this.handleturnOnClick.bind(this);
    this.handleturnOffClick = this.handleturnOffClick.bind(this);
    this.state = {isLamp: false};
  }


  handleturnOnClick() {
    this.setState({isLamp: true});
  }

  handleturnOffClick() {
    this.setState({isLamp: false});
  }


   render(){

    let lamponff;

     if(this.state.isLamp){

           lamponff = <h1>ON</h1>; 

      }else{

           lamponff = <h1>OFF</h1>;
      }

     return (

      <div>
          {lamponff}
          <button onClick={()=>this.handleturnOnClick()}>Switch On</button>
          <button onClick={()=>this.handleturnOffClick()}>Switch Off</button>
      </div> 

     );




    }


}


ReactDOM.render(
  <React.StrictMode>
    <Conditional />
  </React.StrictMode>,
  document.getElementById('root')
);

