import {
  GET_REQUEST,
  GET_REQUEST_API_SUCCESS,
  GET_REQUEST_API_FAILURE,
} from "./actions";

const defaultstate =
  {
    loading: "",
    error: "",
    users: []
  }
;

export default function person(state = defaultstate, action) {
  switch (action.type) {
    case GET_REQUEST:
      return {
        ...state,
        loading: true,
      };
    case GET_REQUEST_API_SUCCESS:
      return {
        ...state,
        loading: false,
        users: action.payload,
        error:''
      };
      case GET_REQUEST_API_FAILURE:
        return {
          ...state,
          loading: false,
          users: [],
          error: action.payload
        };
    default:
      return state;
  }
}
