//post resquest with react redux thunk
//get request with react redux thunk+useeffect

import "./sass/main.css";
import "../node_modules/bootstrap/dist/css/bootstrap.css";
import "../node_modules/bootstrap/dist/js/bootstrap.bundle";
import React, { useState } from "react";
import { BrowserRouter, Route, Switch } from "react-router-dom";
import axios from "axios";

async function Apirequest(JWTToken) {
  return axios
    .get("http://localhost:3001/foods", {
      headers: { Authorization: `Bearer ${JWTToken}` },
    })
    .then((res) => console.log("profile is:", res.data))
    .catch((error) => console.log(error));
}

function App() {
  const storedJwt = localStorage.getItem("token");
  const [jwt, setJwt] = useState(storedJwt || null);
  const [foods, setFoods] = useState([]);
  const [fetchError, setFetchError] = useState(null);

  const getJwt = async () => {
    const { data } = await axios.get(`http://localhost:3001/jwt`);
    localStorage.setItem("token", data.token);
    setJwt(data.token);
  };

  const getFoods = async () => {
    try {
      const { data } = await axios.get(`http://localhost:3001/foods`);
      setFoods(data);
      setFetchError(null);
    } catch (err) {
      setFetchError(err.message);
    }
  };

  return (
    <>
      <section style={{ marginBottom: "10px" }}>
        <button onClick={() => getJwt()}>Get JWT</button>
        {jwt && (
          <pre>
            <code>{jwt}</code>
          </pre>
        )}
      </section>
      <section>
        <button onClick={() => getFoods()}>Get Foods</button>
        <ul>
          {foods.map((food, i) => (
            <li>{food.description}</li>
          ))}
        </ul>
        {fetchError && <p style={{ color: "red" }}>{fetchError}</p>}
      </section>
    </>
  );
}

export default App;
