import React, { useEffect, useState } from "react";
import "../sass/main.css";
import "../../node_modules/bootstrap/dist/css/bootstrap.css";
import "../../node_modules/bootstrap/dist/js/bootstrap.bundle";

import getList from "../services/lists";
import setItem from ""

export default function Home() {

  const [itemInput, setItemInput] = useState('');
  const [list, setList] = useState([]);
  const [visibleBlogs, setVisibleBlogs] = useState([2]);

  const handleClick = () => {
    setVisibleBlogs((prevVisibleBlogs) => prevVisibleBlogs + 3);
  };

  useEffect(() => {
    let mounted = true;
    getList().then((items) => {
      if (mounted) {
        setList(items);
      }
    });
    return () => (mounted = false);
  }, []);

  return (
    <>
      <div class="container" style={{ paddingTop: "10px" }}>
        <div class="row">
          <div class="col-sm">
            <div class="card" style={{ width: "18rem" }}>
              <div class="card-body">
                <div class="mb-3">
                  <label for="exampleFormControlInput1" class="form-label">
                    New Card
                  </label>
                <div class="input-group mb-3">
                  <select class="form-select" id="inputGroupSelect02">
                    <option selected>Choose...</option>
                    <option value="1">One</option>
                    <option value="2">Two</option>
                    <option value="3">Three</option>
                  </select>
                  <label class="input-group-text" for="inputGroupSelect02">
                    Url Image
                  </label>
                </div>
                  <input
                    type="text"
                    class="form-control"
                    id="exampleFormControlInput1"
                    placeholder="Your Name"
                    onChange={event => setItemInput(event.target.value)} value={itemInput}
                  />
                <button
                  type="button"
                  class="btn btn-secondary"
                  style={{ marginTop: "10px" }}
                >
                  Submit
                </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      <div class="container" style={{ paddingTop: "10px" }}>
        <div class="row">
          {list.slice(0, visibleBlogs).map((item, i) => (
            <div class="col-sm">
              <div class="card" style={{ width: "18rem" }}>
                <div class="card-body">
                  <h5 class="card-title">{list[i].name}</h5>
                  <p class="card-text">
                    <img src={list[i].url} style={{ width: "13rem" }} />
                  </p>
                  <a href="#" class="btn btn-primary">
                    Go somewhere
                  </a>
                </div>
              </div>
            </div>
          ))}
        </div>
        <button
          type="button"
          class="btn btn-outline-success"
          style={{ marginTop: "10px" }}
          onClick={handleClick}
        >
          Load More..
        </button>
      </div>
    </>
  );
}
